const input=document.getElementById("search_input"),search_button=document.getElementById("search"),searched_city=document.getElementById("searched_city"),status=document.getElementById("curr_stats"),image=document.getElementById("status_img"),image2=document.getElementById("location_img"),celsius=document.getElementById("c_data"),fahrenheit=document.getElementById("f_data"),humidity=document.getElementById("humidity_data"),wind=document.getElementById("wind_data"),clearButton=document.getElementById("clear"),placeholder=document.getElementById("search_input"),weather=async e=>{let t=`https://weatherapi-com.p.rapidapi.com/current.json?q=${e}`,n={method:"GET",headers:{"X-RapidAPI-Key":"d65614ae20msh90b2dec278f0d53p1ca0e1jsnefaeebd34fb7","X-RapidAPI-Host":"weatherapi-com.p.rapidapi.com"}};try{let a=await fetch(t,n),i=await a.json(),r=i.location.name,c=i.current.condition.text,d=i.current.temp_c,p=i.current.temp_f,m=i.current.humidity,s=i.current.wind_kph,l=i.current.condition.icon;searched_city.innerHTML=`<p>${r}</p>`,status.innerHTML=`<p>${c}</p>`,image.src=l,image2.src="location1.png",celsius.innerHTML=`<p>Temp. in C :   ${d}<span>&#8451;</span></p>`,fahrenheit.innerHTML=`<p>Temp. in F :   ${p}<span>&#8457;</span></p>`,humidity.innerHTML=`<p>Humidity :   ${m}%</p>`,wind.innerHTML=`<p>Wind Speed :   ${s} km/h</p>`,document.getElementById("onResult").play()}catch(h){console.error(h),document.getElementById("onError").play(),alert("Invalid Name !!"),clearWeatherData()}},clearWeatherData=()=>{document.getElementById("onClear").play(),searched_city.innerHTML="NIL",status.innerHTML="NIL",image.src="/wind.png",image2.src="/location2.png",celsius.innerHTML="Temp. in C : NIL",fahrenheit.innerHTML="Temp. in F : NIL",humidity.innerHTML="Humidity (%) : NIL",wind.innerHTML="Wind Speed (Km/h) : NIL",placeholder.value=""};search_button.addEventListener("click",()=>{weather(input.value)}),clearButton.addEventListener("click",clearWeatherData);